import Link from "next/link";
import React, { useState } from "react";
import ReactDOM from "react-dom";
import { Flipper, Flipped } from "react-flip-toolkit";
import { Date } from "./data";

const listData = [...Array(7).keys()];
const createCardFlipId = index => `listItem-${index}`;

const shouldFlip = index => (prev, current) =>
  index === prev || index === current;

const ListItem = ({ data, index, onClick }) => {
    console.log(data);
  return (
    <Flipped
      flipId={createCardFlipId(index)}
      stagger="card"
      shouldInvert={shouldFlip(index)}
    >
      <div className="listItem" onClick={() => onClick(index)}>
        <Flipped inverseFlipId={createCardFlipId(index)}>
          <div className="listItemContent">
            <div>
                <Flipped
                    flipId={`description-${index}-${data.title}`}
                    stagger="card-content"
                    shouldFlip={shouldFlip(index)}
                    delayUntil={createCardFlipId(index)}
                    >
                    <Link href={`/work/${data.id}`}>
                        <a className="font-bold">{data.title}</a>
                    </Link>
                </Flipped>
                <Flipped
                    flipId={`description-${index}-${data.title}`}
                    stagger="card-content"
                    shouldFlip={shouldFlip(index)}
                    delayUntil={createCardFlipId(index)}
                    >
                    <div className="text-sm"><Date startDate={data.startDate} endDate={data.endDate}/></div>
                </Flipped>
                <Flipped
                    flipId={`description-${index}-${data.title}`}
                    stagger="card-content"
                    shouldFlip={shouldFlip(index)}
                    delayUntil={createCardFlipId(index)}
                    >
                    <div className="text-md flex justify-around">
                        {data.tags.slice(0, 2).map(tag =>
                            <div className="bg-red-400 px-4 m-2 rounded">
                                <Link href={`/tags/${tag}`}>
                                    <a>{tag}</a>
                                </Link>
                            </div>
                            )
                        }
                    </div>
                </Flipped>
            </div>
          </div>
        </Flipped>
      </div>
    </Flipped>
  );
};

const ExpandedListItem = ({ data, index, onClick }) => {
  return (
    <Flipped
      flipId={createCardFlipId(index)}
      stagger="card"
      onStart={el => {
        setTimeout(() => {
          el.classList.add("animated-in");
        }, 400);
      }}
    >
      <div className="expandedListItem" onClick={() => onClick(index)}>
        <Flipped inverseFlipId={createCardFlipId(index)}>
          <div className="expandedListItemContent">

            <div>
                <Flipped
                    flipId={`description-${index}-${data.title}`}
                    stagger="card-content"
                    shouldFlip={shouldFlip(index)}
                    delayUntil={createCardFlipId(index)}
                    >
                    <div className="font-bold text-lg">{data.title}</div>
                </Flipped>
                <Flipped
                    flipId={`description-${index}-${data.title}`}
                    stagger="card-content"
                    shouldFlip={shouldFlip(index)}
                    delayUntil={createCardFlipId(index)}
                    >
                    <div className="text-sm"><Date startDate={data.startDate} endDate={data.endDate}/></div>
                </Flipped>
                <Flipped
                    flipId={`description-${index}-${data.title}`}
                    stagger="card-content"
                    shouldFlip={shouldFlip(index)}
                    delayUntil={createCardFlipId(index)}
                    >
                    <div className="text-md">{data.tags.map(tag => tag)}</div>
                </Flipped>
            </div>
            <div className="additional-content">
              {data.description.map(i => (
                  <p>
                      {i}
                  </p>
              ))}
            </div>
          </div>
        </Flipped>
      </div>
    </Flipped>
  );
};

export default function AnimatedList({data}) {
    const [focused, setFocused] = useState(null);
    
    const handleChange = (index) => {
        setFocused(focused === index ? null : index)
    };
  
    return (
      <Flipper
        flipKey={focused}
        className="staggered-list-content"
        spring="gentle"
        staggerConfig={{
          card: {
            reverse: focused !== null
          }
        }}
        decisionData={focused}
      >
        <ul className="list">
          {data.map((item, i) => {
            return (
              <li key={item.id}>
                {i === focused ? (
                  <ExpandedListItem
                    data={data[i]}
                    index={focused}
                    onClick={handleChange}
                  />
                ) : (
                  <ListItem data={data[i]} index={i} key={i} onClick={handleChange} />
                )}
              </li>
            );
          })}
        </ul>
      </Flipper>
    );
}
